import { React, useRef, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlay,
  faAngleLeft,
  faAngleRight,
} from "@fortawesome/free-solid-svg-icons";

const Player = ({ sendingprop2, isPlaying, setisPlaying }) => {
  const audioRef = useRef(null);
  const playerhandler = () => {
    if (isPlaying) {
      audioRef.current.pause();
      setisPlaying(!isPlaying);
    } else {
      audioRef.current.play();
      setisPlaying(!isPlaying);
    }
  };

  const handlertimecontrol = (e) => {
    const duration = e.target.duration;
    const current = e.target.currentTime;
    setsonginfo({ ...songinfo, currentTime: current, duration: duration });
  };
  const [songinfo, setsonginfo] = useState({
    currentTime: null,
    duration: null,
  });
  const getTime = (time) => {
    return (
      Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
    );
  };
  const dragHandler = (e) => {
    setsonginfo({...songinfo,currentTime:e.target.value});
  }
  return (
    <div className="player">
      <div className="time-control">
        <p>{getTime(songinfo.currentTime)}</p>
        <input
          min={0}
          max={setsonginfo.duration}
          value={songinfo.currentTime}
          type="range"
        />
        <p>{getTime(songinfo.duration)} </p>
      </div>
      <div className="player-control">
        <FontAwesomeIcon className="skip-back" size="2x" icon={faAngleLeft} />
        <FontAwesomeIcon
          onClick={playerhandler}
          className="play"
          size="2x"
          icon={faPlay}
        />
        <FontAwesomeIcon
          className="skip-forward"
          size="2x"
          icon={faAngleRight}
        />
      </div>
      <audio
        onTimeUpdate={handlertimecontrol}
        onLoadedMetadata={handlertimecontrol}
        ref={audioRef}
        src={sendingprop2.audio}
        onChange={dragHandler}
      ></audio>
    </div>
  );
};

export default Player;
