import React from 'react';


export default function librarysong({audioRef,song,songs,setcurrentSong,id}) {
    //this is an indiviual song on the libary section
    const songSelector = async() =>
    {
   await setcurrentSong(song);
   audioRef.current.play();
    }
    return (
        <div onClick={songSelector} className="library-song">
        <img src = {song.cover}/>
        <div className='song-description'>
        <h3>{song.name}</h3>
        <h4>{song.artist}</h4>
        </div>
      </div>
    )
}
