import React from "react";
import {playAudio} from '../util/';

export default function librarysong({
  audioRef,
  song,
  songs,
  setcurrentSong,
  id,
  setSongs,
}) {
  //this is an indiviual song on the libary section
  const songSelector = async () => {
    await setcurrentSong(song);
    audioRef.current.play();
    //newSongs is a variable which have all the songs but the selected will be true rest will be false.
    const newSongs = songs.map((song) => {
      if (song.id === id) {
        return {
          ...song,
          active: true,
        };
      } else {
        return {
          ...song,
          active: false,
        };
      }
    });

    setSongs(newSongs);
    playAudio(isPlaying,audioRef);
  };

  return (
    <div
      onClick={songSelector}
      className={`library-song ${song.active ? "selected" : ""}`}
    >
      <img alt="song-cover" src={song.cover} />
      <div className="song-description">
        <h3>{song.name}</h3>
        <h4>{song.artist}</h4>
      </div>
    </div>
  );
}
